// import { useState } from "react"

const Question  = ({input_data,move}) =>{

    //  seteach(input_data[move])
    //  console.log(move)
    return(
        <>
       {
        input_data.map((element,index)=>{
            return(                    
                    <div key={index} className={move===index?"block":"hidden"}>
                       <div className="flex gap-96">
                        <h1>{element.id}</h1>
                        <h1>time:</h1>
                       </div>
                       <div>
                        <h1>{element.question}</h1>
                       </div>
                       <div>
                        {
                            element.option.map((e)=>{
                                return(
                                    <div className="flex">
                                    <input type="radio" />
                                    <h1>{e.slice(2)}</h1>
                                    </div>
                                )
                            })
                        }
                       </div>
                    </div>

            )
        })
       }
        </>
    )
}
export default Question
