// import { useState } from "react"

// import { useState } from "react"

const Question  = ({input_data,move,showselect,shows}) =>{

    //  seteach(input_data[move])
    //  console.log(move)

    return(
        <>
       {
        input_data.map((element,index)=>{
            return(                    
                    <div key={index} className={move===index?"block":"hidden"}>
                       <div className="flex gap-96">
                            <h1>{element.id}</h1>
                            <h1>time:</h1>
                       </div>
                       {/* <div className="h-[25%] mt-4 w-full border-solid border border-sky-400"> */}
                            <h1 className="text-md">{element.question}</h1>
                       {/* </div> */}
                       <div className="border-black  border flex flex-col gap-2">
                        {
                            element.option.map((e,i)=>{
                                return(
                                    <div key={i} className="flex gap-2">
                                        <input type="radio" name="p" checked={shows[element.id]===e}  value={e.slice(0,1)} onChange={(e)=>{showselect((select)=>({...select,[element.id]:e.target.value}))}}/>
                                        <h1>{e}</h1>
                                        {console.log(shows)}
                                    </div>
                                )
                            })
                        }
                       </div>
                    </div>

            )
        })
       }
        </>
    )
}
export default Question
