import { useEffect, useState } from "react";

const ExamPage = ()=>{  
    const [data,Setdata]=useState([])
    const [slide,Setslide]=useState(0)
    // const[active,setactive]=useState(true)
    useEffect(()=>{
        let active=new AbortController();
        const get_data = async () =>{
            try{
                const response= await fetch(`http://localhost:5000/exam`,{
                    signal:active.signal
                })
                const result= await response.json();
                // console.log(result)
                // if(active){
                    Setdata(result)
            }
            catch(error){
                if(error.name==='Aborterror'){
                    console.log(error.name)
                }
            }             
        };
        get_data()
        return ()=>{

        }
    },[]);

        console.log(data)


    return (
       <div className="login-container h-screen w-screen  border-solid border-black">
            <head className="h-[15%]  w-full flex flex-wrap flex-col justify-center items-center bg-[url('../public/img/exam.png')]  bg-no-repeat bg-contain bg-center"></head>

                <main className="h-[85%]  flex flex-wrap  flex-col justify-center items-center ">
                    <div className="exam-container h-[85%] w-1/2 border-2 border-solid border-rose-200">

                        <div className="h-[8%] border flex flex-wrap flex-row justify-between ">


                           {/* <h1></h1> */}
                           <h1>{data[slide].id}</h1>
                            <h1>Time:1:20</h1>

                        </div> 
                        <div className="h-[18%] border flex flex-wrap flex-col justify-center items-center">

                            <h1>{data[slide].question}</h1>    
                        </div>       

                        <div className="h-[57%] border flex flex-wrap flex-row justify-center items-center">
                            <div className="h-[15%] w-full ">
                                <input type="radio" />
                                 {/* {data[slide].option_a} */}
                            </div>
                            <div className="h-[15%] w-full ">
                                <input type="radio" /> 
                                {/* {data[slide].option_b} */}
                            </div>
                            <div className="h-[15%] w-full ">
                                <input type="radio" /> 
                                {/* {data[slide].option_c} */}
                            </div>
                            <div className="h-[15%] w-full ">
                                <input type="radio" /> 
                                {/* {data[slide.option_d]} */}
                            </div>

                        </div>

                        <div className="h-[17%] flex flex-wrap flex-row justify-center items-center">
                            <button className="bg-blue-500 hover:bg-blue0-100 text-white font-bold py-2 
                            px-4 rounded-full" onClick={()=>Setslide(slide+1)}>Next</button>
                            <button className="bg-blue-500 hover:bg-blue-100 text-white 
                            font-bold py-2 px-4 rounded-full" onClick={()=>Setslide(slide-1)} >Prev</button>
                            <button className="bg-blue-500 hover:bg-blue-100 text-white font-bold py-2 px-4 rounded-full"
                                 >Submit</button>
                        </div>

                    </div>
                </main>

         </div>
)}
export default ExamPage;
