// import { useState } from "react"

const Question  = ({input_data,move}) =>{

    //  seteach(input_data[move])
    //  console.log(move)
    return(
        <>
       {
        input_data.map((element,index)=>{
            return(
                <>

                    <div key={index} className={move===index?"block":"hidden"}>
                        <div className="h-[12%] w-[105%] border flex flex-wrap flex-row justify-between ">    
                                { <h1>Q:{element.id}</h1> }


                                    <h1>Time:1:20</h1>
                        </div>
                        <h1 className="text-small">{element.question}</h1>
                        <div  className=" h-[90%] w-[105%] flex flex-col flex-wrap gap-4 ">
                            {
                                element.option.map((opt,i)=>{
                                    return(
                                        <div key={i} className=" h-[18%] w-full  flex flex-row flex-wrap justify-start gap-2  ">
                                            <input type='radio' name="opt"  value={opt.slice(0,1)} onChange={(e)=>{console.log(e.target.value)}}    />
                                            <h1  className="text-center text-small mt-3">{opt.slice(2)}</h1>    
                                        </div>
                                    )
                                })
                            }
                        </div>
                    </div>
                </>
            )
        })
       }
        </>
    )
}
export default Question
