import {useState} from 'react';
const Option = ({input_option,move}) =>{
    const [user_option,setoption]=useState()

    return(
        <>
           {input_option.length>0 && input_option[move].option.map((element,index)=>{

                            return(
                                <div key={index} className="h-[15%] w-full flex gap-2">

                                    <input type="radio" name="option" value={element.slice(0,1)}  onChange={e=>{setoption(e.target.value)}} />
                                    <h1 key={index}> {element.slice(2)} </h1> 
                                    {console.log(user_option)}
                                </div>
                            )

                        })
            }

        </>

    )
}
export default Option;
