import { useState,useRef } from "react"

const Quiz = ({new_data}) =>{
    // console.log(new_data)
    const [each_content,setcontent]=useState({})
    const [slide,Setslide]=useState(0)

    const inter=useRef(null)

    return (

            <div className="exam-container h-[85%] w-1/2 border-2 border-solid border-rose-200">
                <div className="h-[15%] border flex flex-wrap flex-row justify-between ">    
                           {new_data.map((e,index)=>{
                            {/* inter.current && inter.current.classList.toggle("!block") */}
                            inter.current && console.log(inter);
                            return(
                                <div>


                                <h1 key={index} className={"hidden"} ref={inter}>{e.id}</h1>
                                </div>
                                )

                           })}
                            <h1>Time:1:20</h1>
                </div> 
                <div className="h-[18%]  flex flex-wrap flex-col justify-center items-center">

                                    {/* <h1>{[slide].question}</h1>      */}
               </div>    
                <div className="h-[67%] border border-black flex flex-wrap flex-row justify-center items-center">
                    <div className="h-[15%] w-full flex gap-2">
                        <input type="radio" name="option_a"   />
                        <h1>
                            {/* {data.length && data[slide].option_a.slice(2)} */}
                        </h1>                               
                    </div>
                    <div className="h-[15%] w-full flex gap-2">
                        <input type="radio" name="option_b" /> 
                        <h1></h1>
                    </div>z 
                    <div className="h-[15%] w-full flex gap-2">
                      <input type="radio" name="option_c"  />     
                   </div>
                    <div className="h-[15%] w-full flex gap-2 ">
                            <input type="radio" name="option_d" />
                     </div>
                    <div className="h-[30%]  flex flex-wrap flex-row justify-center items-center gap-20">
                        <button className={slide === 0 ? "small outlined" : "small"}  onClick={()=>{Setslide(slide+1);inter.current.classList.toggle("!block")}}>next</button>
                        <button className={slide === 0 ? "small outlined" : "small"}  onClick={()=>Setslide(slide-1)} >Prev</button>
                        <button className="bg-blue-500 hover:bg-blue-100 text-white font-bold py-2 px-4 rounded-full"
                           >Submit</button>
                    </div>
                </div>

            </div>  


    )
}
export default Quiz;
