// import { useState } from "react"

import { useState } from "react"

const Question  = ({input_data,move}) =>{

    //  seteach(input_data[move])
    //  console.log(move)
    const [select,setselect]=useState({})
    return(
        <>
       {
        input_data.map((element,index)=>{
            return(                    
                    <div key={index} className={move===index?"block":"hidden"}>
                       <div className="flex gap-96">
                            <h1>{element.id}</h1>
                            <h1>time:</h1>
                       </div>
                       {/* <div className="h-[25%] mt-4 w-full border-solid border border-sky-400"> */}
                            <h1 className="mt-4 text-md">{element.question}</h1>
                       {/* </div> */}
                       <div className="border-black mt-6  border flex flex-col gap-2">
                        {
                            element.option.map((e)=>{
                                return(
                                    <div className="flex gap-2">
                                    <input type="radio" name="e"  value={e.slice(0,1)} onchange={(e)=>{setselect({...select,[element.id]:e.target.value})}} />
                                    </div>
                                )
                            })
                        }
                       </div>
                    </div>

            )
        })
       }
        </>
    )
}
export default Question
