// import { useState } from "react"

const Question  = ({input_data,move}) =>{
    // const[each,seteach]=useState()

    //  seteach(input_data[move])
    //  console.log(move)
    return(
        <>
       {
        input_data.map((element,index)=>{
            return(
                <div className={move===index?"block":"hidden"}>
                    <h1 key={index}>{element.question}</h1>
                    <div key={index} className="border border-blue-500 h-[92%] w-[105%] flex flex-col flex-wrap gap-4 ">
                        {
                            element.option.map((opt,i)=>{
                                return(
                                    <div key={i} className="border border-solid border-black h-[20%] w-full  flex flex-row flex-wrap justify-start gap-2  ">
                                        <input type='radio'  />
                                        <h1 key={i} className="text-center">{opt.slice(2)}</h1>
                                    </div>
                                )
                            })
                        }
                    </div>
                </div>
            )
        })
       }
        </>
    )
}
export default Question
