import { useState } from "react"
import Question from "./question"
// import Option from "./option"
const Quiz = ({new_data}) =>{
    // const [user_option,setoption]=useState()
    // const [fresh_data,setfreshdata]=useState(new_data)
    const [show,setShow]=useState(0)
    // setfreshdata(new_data)


    return(
            <div className="exam-container h-[85%] w-1/2 border-2 border-solid border-rose-200">
                <div className="h-[9%] border flex flex-wrap flex-row justify-between ">    
                        { new_data.length>0 && <h1 > Q:{new_data[show].id}</h1> }


                            <h1>Time:1:20</h1>
                </div>
                <div className="h-[76%] w-full border border-solid border-black flex">
                    <Question input_data={new_data} move={show} />           


                </div>
                <div className="h-[15%] border border-black flex flex-wrap flex-row justify-center items-center gap-20">
                            <button disabled={!(show>=0 && show<new_data.length-1)}  onClick={()=>{setShow(show+1)}}>next</button> 
                            <button disabled={(show===0)} onClick={()=>{setShow(show-1)}} >Prev</button>
                            <button className="bg-blue-500 hover:bg-blue-100 text-white font-bold py-2 px-4 rounded-full"
                            >Submit</button>

                </div>
            </div>
        )    
}

export default Quiz;
