import { useEffect, useState } from "react";

const ExamPage = ()=>{  
    const [data,setdata]=useState({})
    const [slide,Setslide]=useState(0)
    const[clickoption,setoption]=useState('')
    useEffect(()=>{
        const get_data = async () =>{
            let url='http://localhost:5000/exam'
            const response= await fetch(url)
            const result= await response.json();
            // console.log(result)
            each_data(result)
        };          
        // const each_data=(input_data)=>{
        //    for(let element of input_data){
        //         setdata(element)
        //    }
        // }        
        get_data()
        console.log(data)
    },[]);


    return (
       <div className="login-container h-screen w-screen  border-solid border-black">
            <head className="h-[15%]  w-full flex flex-wrap flex-col justify-center items-center bg-[url('../public/img/exam.png')]  bg-no-repeat bg-contain bg-center"></head>

                <main className="h-[85%]  flex flex-wrap  flex-col justify-center items-center ">
              <div className="exam-container h-[85%] w-1/2 border-2 border-solid border-rose-200">
                    <Quiz i />
                        <div className="h-[18%] border flex flex-wrap flex-col justify-center items-center">

                            {/* <h1>{data[slide].question}</h1>      */}
                        </div>       

                        <div className="h-[57%] border flex flex-wrap flex-row justify-center items-center">
                            <div className="h-[15%] w-full flex gap-2">
                                <input type="radio" name="option_a" value={data.length && data[slide].option_a.slice(0,1)}
                                     onChange={(e)=>setoption(e.target.value)}  />
                                 {/* {console.log(data.length&&data[slide].option_a.slice(0,1),clickoption)} */}
                                 <h1>
                                    {data.length && data[slide].option_a.slice(2)}
                                 </h1> 

                            </div>
                            <div className="h-[15%] w-full flex gap-2">
                                <input type="radio" name="option_b" value={data.length && data[slide].option_b.slice(0,1)}
                                    checked={data.length && data[slide].option_b===clickoption } onChange={e=>setoption(e.target.value)} /> 
                                <h1>
                                    {data.length && data[slide].option_b.slice(2)}
                                </h1>
                            </div>
                            <div className="h-[15%] w-full flex gap-2">
                                <input type="radio" name="option_c" value={data.length && data[slide].option_c} 
                                 checked={data.length && data[slide].option_c===clickoption} onChange={e=>setoption(e.target.value)} /> 
                                <h1>
                                    {data.length && data[slide].option_c.slice(2)}

                                </h1>
                            </div>
                            <div className="h-[15%] w-full flex gap-2 border border-solid border-black">
                                <input type="radio" name="option_d" checked={data.length && data[slide].option_d==clickoption} value={data.length && data[slide].option_d} onChange={e=>setoption(e.target.value)}  /> 
                                {data.length && data[slide].option_d.slice(2)}
                            </div>

                        </div>

                        <div className="h-[17%] flex flex-wrap flex-row justify-center items-center gap-20">
                            <button className={slide === 0 ? "small outlined" : "small"} disabled={slide===data.length}  onClick={()=>Setslide(slide+1)}>next</button>
                            <button className={slide === 0 ? "small outlined" : "small"} disabled={slide===0?`hidden`:`block`} onClick={()=>Setslide(slide-1)} >Prev</button>
                            <button className="bg-blue-500 hover:bg-blue-100 text-white font-bold py-2 px-4 rounded-full"
                                >Submit</button>
                        </div>

                    </div>
                </main>

         </div>
)}
export default ExamPage;
